import axios from 'axios';
import React, { useEffect, useState } from 'react'
const API_KEY=process.env.REACT_APP_GIPPY_API_KEY;

const Random = () => {
    const[gif,setGif]=useState('');

    async function fetchData(){
        const url=`https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`
        const {data}= await axios.get(url);
        const imageUrl=data.data.images.downsized_large.url;
        setGif(imageUrl);
    }

    useEffect(()=>{
        fetchData();
    },[])

    function generateHandler(){
        fetchData();
    }
  return (
    <div className='bg-[#7eeb7c] rounded-lg w-[40vw] mt-4 flex flex-col items-center shadow-lg gap-y-5'> 
        <h1 className=' rounded-lg  w-[20vw] text-center text-xl font-bold py-2 underline'>Random Gif</h1>
        <img src={gif} alt='gif' className='border-2 ' />
        <button onClick={generateHandler} className='bg-green-300 rounded-lg py-2 w-3/4 mb-4 font-semibold text-lg shadow-lg cursor-pointer'>Generate</button>
    </div>
  )
}

export default Random
